function uprulefile() {
    try {
        let jiasu = 'https://github.moeyy.xyz/';
        var ruletxt = fetch('https://rxsub.eu.org/hiker/api/cr.txt', {});
        var rulejs = fetch('https://rxsub.eu.org/hiker/rules/xyq/zywcj.js', {});
    } catch (e) {
        rulejs = "";
        ruletxt = "";
    }
    if (rulejs.search(/lazyRule/) != -1) {
        writeFile("hiker://files/rules/xyq/rxcr.js", rulejs);
        writeFile("hiker://files/rules/xyq/rxcr.txt", ruletxt);
    } else {
        setError('规则文件获取失败，可能网络有问题。')
    };
};

//检测是否有规则与JS文件
if (fileExist("hiker://files/rules/xyq/rxcr.js") && fileExist("hiker://files/rules/xyq/rxcr.txt")) {
