        writeFile("hiker://files/rules/xyq/rxcr.js", fetch('https://rxsub.eu.org/hiker/rules/xyq/zywcj.js', {}));
    }
    if (lac.search(/lazyRule/) != -1) {
        eval(lac);
        if (filter(base64Decode('VklQ'))) {
            zywpre();
        } else {
            uprulefile();
        };
    } else {
        //如果本地文件不对将尝试自修复
        uprulefile();
        eval(fetch('hiker://files/rules/xyq/rxcr.js'));
        zywpre();
    };
} else {
    uprulefile();
    //执行预处理
    eval(fetch('hiker://files/rules/xyq/rxcr.js'));
